
public class NormalDoWhileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
	
		
		
		//Print 1 to 10 
		int i=1;
		
		while(i<=10)
		{
			System.out.println(i);
			i++;
		}
		
		
		System.out.println("------------------");
		//Print 10 to 1
		int j=10;
		while(j>0)
		{
			System.out.println(j);
			j--;
		}
		
		System.out.println("------------------");
		
		int k=1;
		do {
			k++;
			System.out.println(k);
			
		}while(k<=10);
		
		

	}

}
